  }
});

$(".introjs-skipbutton").live("click", function (e) {
  document.cookie = "darwinbox_guid_tour_" + cookie_page_name + "=1; expires=18 Dec 2017 12:00:00 UTC; path=" + page_full_url + ";";
});

function getCookie(cname) {
  var name = cname + "=";
  var ca = document.cookie.split(';');
  for (var i = 0; i < ca.length; i++) {
    var c = ca[i];
    while (c.charAt(0) == ' ')
      c = c.substring(1);
    if (c.indexOf(name) == 0)
      return c.substring(name.length, c.length);
  }
  return "";
}
function isInt(value)
{
  return !isNaN(value) && parseInt(Number(value)) == value && !isNaN(parseInt(value, 10));
}

function previewExpenseAttachment(el){
  var key = $(el).attr('file-key') || $(el).parent().attr('file-key');
  var bucket = $(el).attr('file-bucket') || $(el).parent().attr('file-bucket');
  var text = $(el).text();
  var url = '/employee/previewlink/';
  // var element = document.createElement('a');
  // $(element).addClass('shadowbox');
  shadowid = $(el).attr('info-id');
   $('body').append('<a id="'+shadowid+'"></a>');
  $.ajax({
    type: 'POST',
    url: url,
    data: {key: key, bucket:bucket, name:text},
    success: function (data) {
      data = data.trim();
      var previewurl = 'https://docs.google.com/viewer?url='+encodeURIComponent(data)+'&embedded=true';
      var shadowel = $("#"+shadowid);
      shadowel.attr('href',previewurl) ;
      Shadowbox.setup(shadowel.get(0));
      shadowel.get(0).click();
    }
  });

}

